pontuação=[]
def calcular_pontuacao(estatisticas_partida):
    for jogador in estatisticas_partida:
        pontos = 0
        passes_errados = jogador["passes_tentados"] - jogador["passes_completos"]
        finalizacao_fora = jogador["finalizacoes"] - jogador["finalizacoes_no_alvo"]

        pontos += jogador["gols"] * 8
        pontos += jogador["assistencias"] * 5
        pontos -= passes_errados * 0.1
        pontos += finalizacao_fora * 0.8
        pontos += jogador["finalizacoes_no_alvo"] * 1.2
        pontos -= jogador["cartao_amarelo"] * 1
        pontos -= jogador["cartao_vermelho"] * 3
        pontos += jogador["desarmes"] * 1.2

        if jogador["minutos"] >= 75:
            pontos += pontos * 0.10

        jogador["pontuacao"] = round(pontos, 2)
        print(f"{jogador['nome']} ({jogador['clube']}) - Pontuação: {jogador['pontuacao']}")

print("Pontuação dos jogadores:")
calcular_pontuacao(estatisticas_partida)

jogadores_ordenados = sorted(estatisticas_partida, key=lambda x: x["pontuacao"], reverse=True)
print("Jogadores maiores pontuadores para menores:")
for jogador in jogadores_ordenados:
    print(f"{jogador['nome']} ({jogador['clube']}): {jogador['pontuacao']} pontos")

print("Top 3 jogadores:")
for i in range(3):
    jogador = jogadores_ordenados[i]
    print(f"{i+1}º: {jogador['nome']} ({jogador['clube']}) : {jogador['pontuacao']} pontos")

print("Últimos 3 jogadores:")
for i in range(1, 4):
    jogador = jogadores_ordenados[-i]
    print(f"{len(jogadores_ordenados)-i+1}º: {jogador['nome']} ({jogador['clube']}) :{jogador['pontuacao']} pontos")
